Imports items from partial disposal reports. This function should not be called dirctly by programmers but rather through the other disposal approval api's.
DECLARE t_report asset_report; t_import asset_report; BEGIN
SELECT * INTO t_report from asset_report where id = in_id;
if t_report.report_class <> 4 THEN RETURN FALSE; END IF;
SELECT * INTO t_import FROM asset_report__begin_import (t_report.asset_class::int, t_report.report_date);
PERFORM asset_report__import( ai.description, ai.tag, ai.purchase_value * ( 100 - rld.percent_disposed ) / 100, ai.salvage_value * ( 100 - rld.percent_disposed ) / 100, ai.usable_life, ai.purchase_date, ai.start_depreciation, ai.location_id, ai.department_id, ai.asset_account_id, ai.dep_account_id, ai.exp_account_id, ai.asset_class_id, ai.invoice_id, t_import.id, r.accum_depreciation * ( 100 - rld.percent_disposed ) / 100, TRUE) FROM asset_item ai JOIN asset_report__get_disposal(t_report.id) r ON (ai.id = r.id) JOIN asset_report_line rl ON (rl.asset_id = ai.id AND rl.report_id = in_id) join asset_rl_to_disposal_method rld ON (rl.report_id = rld.report_id and ai.id = rld.asset_id) where (rld.percent_disposed is null or percent_disposed < 100) and ai.obsolete_by is null; RETURN TRUE; END;

