INSERT INTO asset_report_line (asset_id, report_id, amount, department_id, warehouse_id) SELECT ai.id, in_report_id, asset_dep__straight_line_base( ai.usable_life, --months months_passed(coalesce(max(report_date), start_depreciation), in_report_date), purchase_value - salvage_value, coalesce(sum(l.amount), 0)), ai.department_id, ai.location_id FROM asset_item ai LEFT JOIN asset_report_line l ON (l.asset_id = ai.id and l.amount > 0) LEFT JOIN asset_report r ON (l.report_id = r.id) WHERE ai.id = ANY(in_asset_ids) GROUP BY ai.id, ai.start_depreciation, ai.purchase_date, ai.purchase_value, ai.salvage_value, ai.department_id, ai.location_id, ai.usable_life;
