INSERT INTO transactions (reference, description, transdate, approved, trans_type_code) SELECT setting_increment('glnumber'), 'Asset Report ' || asset_report.id, report_date, false, 'fd' FROM asset_report WHERE asset_report.id = in_id;
-- Clear cumulative depreciation account INSERT INTO acc_trans (chart_id, trans_id, amount_bc, curr, amount_tc, approved, transdate) SELECT a.dep_account_id, currval('id')::int, sum(r.accum_depreciation) * -1, defaults_get_defaultcurrency(), sum(r.accum_depreciation) * -1, TRUE, r.disposed_on FROM asset_report__get_disposal(in_id) r JOIN asset_item a ON (r.id = a.id) GROUP BY a.dep_account_id, r.disposed_on HAVING sum(r.accum_depreciation) <> 0;
-- Add cash from sale(=disposal) INSERT INTO acc_trans (chart_id, trans_id, amount_bc, curr, amount_tc, approved, transdate) SELECT in_cash_acct, currval('id')::int, sum(r.disposal_amt) * -1, defaults_get_defaultcurrency(), sum(r.disposal_amt) * -1, TRUE, r.disposed_on FROM asset_report__get_disposal(in_id) r JOIN asset_item ai ON (r.id = ai.id) GROUP BY r.disposed_on HAVING sum(r.disposal_amt) <> 0;
-- GAIN is negative since it is a debit INSERT INTO acc_trans (chart_id, trans_id, amount_bc, curr, amount_tc, approved, transdate) SELECT case when sum(r.gain_loss) > 0 THEN in_loss_acct else in_gain_acct end, currval('id')::int, sum(r.gain_loss), defaults_get_defaultcurrency(), sum(r.gain_loss), TRUE, r.disposed_on FROM asset_report__get_disposal(in_id) r JOIN asset_item ai ON (r.id = ai.id) GROUP BY r.disposed_on;
-- Clear asset from asset account INSERT INTO acc_trans (chart_id, trans_id, amount_bc, curr, amount_tc, approved, transdate) SELECT a.asset_account_id, currval('id')::int, sum(r.purchase_value), defaults_get_defaultcurrency(), sum(r.purchase_value), TRUE, r.disposed_on FROM asset_report__get_disposal(in_id) r JOIN asset_item a ON (r.id = a.id) GROUP BY a.asset_account_id, r.disposed_on HAVING sum(r.purchase_value) <> 0;
SELECT TRUE;

