DECLARE t_alloc numeric := 0; t_cogs numeric := 0; t_inv invoice; t_cp_end_date date; t_transdate date; t_avail numeric; BEGIN
IF in_qty > 0 THEN return (cogs__reverse_ap(in_parts_id, in_qty * -1))[1] * in_lastcost; END IF;
SELECT end_date INTO t_cp_end_date FROM account_checkpoint ORDER BY end_date DESC LIMIT 1;
FOR t_inv IN SELECT i.* FROM invoice i JOIN transactions txn ON txn.id = i.trans_id AND txn.approved WHERE qty + allocated > 0 -- exclude 'ap', because we don't want AP reversals -- @@@BUG? what about inventory report items? AND txn.table_name <> 'ap' AND parts_id = in_parts_id ORDER BY txn.transdate, txn.id, i.id LOOP t_avail := t_inv.qty + t_inv.allocated; SELECT coalesce(in_transdate, transdate) INTO t_transdate FROM transactions WHERE id = t_inv.trans_id; IF t_alloc < in_qty THEN RAISE EXCEPTION 'TOO MANY ALLOCATED'; ELSIF t_alloc = in_qty THEN return t_alloc; ELSIF (in_qty + t_alloc) * -1 <= t_avail THEN UPDATE invoice SET allocated = allocated + (in_qty + t_alloc) WHERE id = t_inv.id;
INSERT INTO acc_trans (chart_id, transdate, amount_bc, curr, amount_tc, invoice_id, approved, trans_id) SELECT expense_accno_id, CASE WHEN t_transdate > coalesce(t_cp_end_date, t_transdate - 1) THEN t_transdate ELSE t_cp_end_date + '1 day'::interval END, (in_qty + t_alloc) * in_lastcost, defaults_get_defaultcurrency(), (in_qty + t_alloc) * in_lastcost, t_inv.id, true, t_inv.trans_id FROM parts WHERE id = t_inv.parts_id AND inventory_accno_id IS NOT NULL AND expense_accno_id IS NOT NULL UNION SELECT inventory_accno_id, CASE WHEN t_transdate > coalesce(t_cp_end_date, t_transdate - 1) THEN t_transdate ELSE t_cp_end_date + '1 day'::interval END, -1*(in_qty + t_alloc) * in_lastcost, defaults_get_defaultcurrency(), -1*(in_qty + t_alloc) * in_lastcost, t_inv.id, true, t_inv.trans_id FROM parts WHERE id = t_inv.parts_id AND inventory_accno_id IS NOT NULL AND expense_accno_id IS NOT NULL;
t_cogs := t_cogs + (in_qty + t_alloc) * in_lastcost; return in_qty * -1; ELSE UPDATE invoice SET allocated = qty * -1 WHERE id = t_inv.id; t_cogs := t_cogs + t_avail * in_lastcost;
INSERT INTO acc_trans (chart_id, transdate, amount_bc, curr, amount_tc, invoice_id, approved, trans_id) SELECT expense_accno_id, CASE WHEN t_transdate > coalesce(t_cp_end_date, t_transdate - 1) THEN t_transdate ELSE t_cp_end_date + '1 day'::interval END, -1*t_avail * in_lastcost, defaults_get_defaultcurrency(), -1*t_avail * in_lastcost, t_inv.id, true, t_inv.trans_id FROM parts WHERE id = t_inv.parts_id AND inventory_accno_id IS NOT NULL AND expense_accno_id IS NOT NULL UNION SELECT inventory_accno_id, CASE WHEN t_transdate > coalesce(t_cp_end_date, t_transdate - 1) THEN t_transdate ELSE t_cp_end_date + '1 day'::interval END, t_avail * in_lastcost, defaults_get_defaultcurrency(), t_avail * in_lastcost, t_inv.id, true, t_inv.trans_id FROM parts WHERE id = t_inv.parts_id AND inventory_accno_id IS NOT NULL AND expense_accno_id IS NOT NULL; t_alloc := t_alloc + t_avail; t_cogs := t_cogs + t_avail * in_lastcost; END IF;
END LOOP;
RETURN t_alloc; END;

DECLARE retval numeric;
        r_cogs numeric[];
        t_inv invoice;
        t_adj numeric;
        t_transdate date;
BEGIN

SELECT * INTO t_inv FROM invoice
 WHERE id = in_invoice_id;

IF t_inv.qty + t_inv.allocated = 0 THEN
   return 0;
END IF;

PERFORM 1 FROM parts
         WHERE t_inv.parts_id = parts.id
               AND parts.inventory_accno_id IS NOT NULL;

IF NOT FOUND THEN
   -- the part doesn't have an associated inventory account: it's a service.
   return 0;
END IF;

IF t_inv.qty < 0 THEN -- normal COGS

    SELECT cogs__add_for_ap(i.parts_id, i.qty + i.allocated, i.sellprice, in_transdate)
      INTO retval
      FROM invoice i
      JOIN parts p ON p.id = i.parts_id
     WHERE i.id = $1;

    UPDATE invoice
       SET allocated = allocated + retval
     WHERE id = $1;
ELSE -- reversal

   r_cogs := cogs__reverse_ap(t_inv.parts_id, t_inv.qty + t_inv.allocated);

   UPDATE invoice
      SET allocated = allocated + r_cogs[1]
    WHERE id = in_invoice_id;

   t_adj := t_inv.sellprice * r_cogs[1] + r_cogs[2];

   SELECT coalesce(in_transdate, transdate) INTO t_transdate FROM transactions
    WHERE id = t_inv.trans_id;

   INSERT INTO acc_trans
          (chart_id, trans_id, approved,  amount_bc, curr, amount_tc, transdate, invoice_id)
   SELECT p.inventory_accno_id, t_inv.trans_id, true, t_adj,
          defaults_get_defaultcurrency(), t_adj, t_transdate,
          in_invoice_id
     FROM parts p
    WHERE id = t_inv.parts_id
    UNION
   SELECT p.expense_accno_id, t_inv.trans_id, true, t_adj * -1,
          defaults_get_defaultcurrency(), t_adj * -1, t_transdate,
          in_invoice_id
     FROM parts p
    WHERE id = t_inv.parts_id;
   retval := r_cogs[1];

END IF;

RETURN retval;

END;

section.para:

        Function Properties

        Language
        Return Type

         PLPGSQL
         numeric[]



        This function accepts a parts_id and a quantity, and iterates through AP
records in order, calculating COGS on a FIFO basis and returning it to the
application to attach to the current transaction. Modifies the `invoice`
records `allocated` values.

Return values are an array of {allocated, cogs}.


DECLARE t_alloc numeric := 0;
        t_cogs numeric := 0;
        t_inv record;
        t_avail numeric;
BEGIN

FOR t_inv IN
    SELECT i.*
      FROM invoice i
           JOIN transactions txn
                ON txn.id = i.trans_id
     WHERE qty + allocated < 0
           AND i.parts_id = in_parts_id
           AND txn.approved
           -- exclude 'ar', because we don't want AR reversals
           -- @@@BUG? what about inventory report items?
           AND txn.table_name <> 'ar'
  ORDER BY txn.transdate asc, txn.id asc, i.id asc
LOOP
   t_avail := (t_inv.qty + t_inv.allocated) * -1;
   IF t_alloc > in_qty THEN
       RAISE EXCEPTION 'TOO MANY ALLOCATED';
   ELSIF t_alloc = in_qty THEN
       return ARRAY[t_alloc, t_cogs];
   ELSIF (in_qty - t_alloc) <= t_avail THEN
       UPDATE invoice SET allocated = allocated + (in_qty - t_alloc)
        WHERE id = t_inv.id;
       t_cogs := t_cogs + (in_qty - t_alloc) * t_inv.sellprice;
       t_alloc := in_qty;
       return ARRAY[t_alloc, t_cogs];
   ELSE
       UPDATE invoice SET allocated = qty * -1
        WHERE id = t_inv.id;
       t_cogs := t_cogs + (t_avail * t_inv.sellprice);
       t_alloc := t_alloc + t_avail;
   END IF;
END LOOP;

RETURN ARRAY[t_alloc, t_cogs];

END;

para.programlisting:
DECLARE
   t_cogs numeric[];
   t_inv invoice;
   t_part parts;
   t_ar ar;
   t_transdate date;
   t_override_cogs int;
BEGIN

SELECT * INTO t_inv FROM invoice WHERE id = in_invoice_id;
SELECT * INTO t_part FROM parts WHERE id = t_inv.parts_id;
SELECT * INTO t_ar FROM ar WHERE id = t_inv.trans_id;
SELECT transdate INTO t_transdate FROM transactions WHERE id = t_inv.trans_id;

IF t_ar.is_return THEN
   t_override_cogs = (setting_get('ar_return_account_id')).value::int;
END IF;

IF t_part.inventory_accno_id IS NULL THEN
   RETURN 0;
END IF;

IF t_inv.qty + t_inv.allocated = 0 THEN
   return 0;
END IF;

IF t_inv.qty > 0 THEN
   t_cogs := cogs__add_for_ar(t_inv.parts_id, t_inv.qty + t_inv.allocated);
ELSE
   t_cogs := cogs__reverse_ar(t_inv.parts_id, t_inv.qty + t_inv.allocated);
END IF;


UPDATE invoice set allocated = allocated - t_cogs[1]
 WHERE id = in_invoice_id;

SELECT CASE WHEN t_transdate > coalesce(max(end_date), t_transdate - 1)
            THEN t_transdate
            ELSE max(end_date) + '1 day'::interval
        END
  INTO t_transdate
  from account_checkpoint td;


INSERT INTO acc_trans
       (trans_id, chart_id, approved, amount_bc,
        curr, amount_tc, transdate,  invoice_id)
VALUES (t_inv.trans_id, COALESCE(t_override_cogs,
                                 CASE WHEN t_inv.qty < 0 AND t_ar.is_return
                                      THEN t_part.returns_accno_id
                                      ELSE t_part.expense_accno_id
                                      END), TRUE, t_cogs[2] * -1,
        defaults_get_defaultcurrency(), t_cogs[2] * -1, t_transdate, t_inv.id),
       (t_inv.trans_id, t_part.inventory_accno_id, TRUE, t_cogs[2],
        defaults_get_defaultcurrency(), t_cogs[2], t_transdate, t_inv.id);

RETURN t_cogs[1];

END;

section.para:

        Function Properties

        Language
        Return Type

         PLPGSQL
         numeric[]



        This function iterates through invoice rows attached to ap transactions and
allocates to them on a first-in first-out basis.  The sort of pseudo-"COGS"
value is returned to the application for further handling.

DECLARE t_alloc numeric :=0;
        t_realloc numeric;
        t_reversed numeric;
        t_inv invoice;
        t_cogs numeric :=0;
        retval numeric[];
BEGIN

-- Move allocation to other purchase lines
FOR t_inv IN
    SELECT i.*
      FROM invoice i
           JOIN transactions txn
                ON txn.id = i.trans_id
  WHERE qty + allocated < 0
        AND parts_id = in_parts_id
        AND txn.approved
         -- exclude 'ar', because we don't want AR reversals
         -- @@@BUG? what about inventory report items?
        AND txn.table_name <> 'ar'
  ORDER BY txn.transdate, txn.id, i.id
LOOP
   t_realloc := least(in_qty - t_alloc, -1 * (t_inv.allocated + t_inv.qty));
   UPDATE invoice SET allocated = allocated + t_realloc
    WHERE id = t_inv.id;
   t_alloc := t_alloc + t_realloc;
   t_cogs := t_cogs + t_realloc * t_inv.sellprice;

   IF t_alloc > in_qty THEN
       RAISE EXCEPTION 'TOO MANY ALLOCATED';
   ELSIF t_alloc = in_qty THEN
       return ARRAY[-1 * t_alloc, t_cogs];
   END IF;
END LOOP;

-- No more items in stock to move allocation to?
-- * Put AR invoices in back-order
FOR t_inv IN
    SELECT i.*
      FROM invoice i
           JOIN transactions txn
                ON txn.id = i.trans_id
     WHERE allocated < 0
           AND txn.approved
           -- exclude 'ap', because we don't want AP reversals
           -- @@@BUG? what about inventory report items?
           AND txn.table_name <> 'ap'
           AND parts_id = in_parts_id
  ORDER BY txn.transdate, txn.id, i.id
LOOP
   t_reversed := least(in_qty - t_alloc, -1 * t_inv.allocated);
   UPDATE invoice SET allocated = allocated + t_reversed
    WHERE id = t_inv.id;
   t_alloc := t_alloc + t_reversed;

   IF t_alloc > in_qty THEN
       RAISE EXCEPTION 'TOO MANY ALLOCATED';
   ELSIF t_alloc = in_qty THEN
       RETURN ARRAY[-1 * t_alloc, t_cogs];
   END IF;
END LOOP;


RAISE EXCEPTION 'TOO FEW TO ALLOCATE';
END;


        Function Properties

        Language
        Return Type

         PLPGSQL
         numeric[]



       This function accepts a part id and quantity to reverse.  It then iterates
backwards over AP related records, calculating COGS.  This does not save COGS
but rather returns it to the application to save. It does however, modify the
`invoice` records.

Return values are an array of {allocated, cogs}.


DECLARE t_alloc numeric := 0; -- qty to reverse (negative)
        t_cogs numeric := 0;
        t_inv invoice;
        t_reversed numeric;
        t_reallocated numeric;
BEGIN

IF in_qty = 0 THEN
   RETURN ARRAY[0, 0];
END IF;

-- First satisfy invoices in back-order
FOR t_inv IN
  SELECT i.*
    FROM invoice i
         JOIN transactions txn
              ON i.trans_id = txn.id
   WHERE qty + allocated > 0
         AND txn.approved
         -- exclude 'ap', because we don't want AP reversals
         -- @@@BUG? what about inventory report items?
         AND txn.table_name <> 'ap'
         AND parts_id = in_parts_id
ORDER BY txn.transdate ASC, txn.id ASC, i.id ASC
LOOP
   t_reallocated := least(t_alloc - in_qty, t_inv.qty + t_inv.allocated);
   UPDATE invoice
      SET allocated = allocated - t_reallocated
    WHERE id = t_inv.id;
   t_alloc := t_alloc - t_reallocated;

   IF t_alloc < in_qty THEN
      RAISE EXCEPTION 'TOO MANY ALLOCATED (1)';
   ELSIF t_alloc = in_qty THEN
      RETURN ARRAY[t_alloc, 0];
   END IF;
END LOOP;

-- No (more) invoices in back-order?
-- * Reverse allocation from AP invoices
FOR t_inv IN
    SELECT i.*
      FROM invoice i
           JOIN transactions txn
                ON txn.id = i.trans_id
     WHERE allocated > 0
           AND txn.approved
           -- exclude 'ar', because we don't want AR reversals
           -- @@@BUG? what about inventory report items?
           AND txn.table_name <> 'ar'
           AND parts_id = in_parts_id
           -- the sellprice check is here because of github issue #4791:
           -- when a negative number of assemblies has been "stocked",
           -- reversal of a sales invoice for that part, fails.
           AND sellprice IS NOT NULL
  ORDER BY txn.transdate DESC, txn.id DESC, i.id DESC
LOOP
   t_reversed := least((in_qty - t_alloc) * -1, t_inv.allocated);
   UPDATE invoice SET allocated = allocated - t_reversed
    WHERE id = t_inv.id;
   t_cogs := t_cogs - t_reversed * t_inv.sellprice;
   t_alloc := t_alloc - t_reversed;

   IF t_alloc < in_qty THEN
       RAISE EXCEPTION 'TOO MANY ALLOCATED';
   ELSIF t_alloc = in_qty THEN
       RETURN ARRAY[t_alloc, t_cogs];
   END IF;
END LOOP;

RAISE EXCEPTION 'TOO FEW TO ALLOCATE';
END;


        Function Properties

        Language
        Return Type

         PLPGSQL
         company



        Saves a company.  Returns the id number of the record stored.

DECLARE t_entity_id INT;
        t_control_code TEXT;
        t_retval COMPANY;
BEGIN

        IF in_control_code IS NULL THEN
                t_control_code := setting_increment('entity_control');
        ELSE
                t_control_code := in_control_code;
        END IF;

        UPDATE entity
        SET name = in_legal_name,
                control_code = t_control_code,
                country_id   = in_country_id
        WHERE id = in_entity_id;

        IF FOUND THEN
                t_entity_id = in_entity_id;
        ELSE
                INSERT INTO entity (name, control_code,country_id)
                VALUES (in_legal_name, t_control_code,in_country_id);
                t_entity_id := currval('entity_id_seq');
        END IF;

        UPDATE company
        SET legal_name = in_legal_name,
                tax_id = in_tax_id,
                sic_code = in_sic_code,
                sales_tax_id = in_sales_tax_id,
                license_number = in_license_number
        WHERE entity_id = t_entity_id;


        IF NOT FOUND THEN
                INSERT INTO company(entity_id, legal_name, tax_id, sic_code,
                                    sales_tax_id, license_number)
                VALUES (t_entity_id, in_legal_name, in_tax_id, in_sic_code,
                        in_sales_tax_id, in_license_number);

        END IF;
        SELECT * INTO t_retval FROM company WHERE entity_id = t_entity_id;
        RETURN t_retval;
END;

