DECLARE inv inventory_report; t_trans_id int; BEGIN
SELECT * INTO inv FROM inventory_report where id = in_id;
IF inv.trans_id IS NOT NULL THEN -- already approved RETURN inv; END IF;
INSERT INTO transactions (id, description, transdate, reference, approved, trans_type_code, table_name) VALUES (nextval('id'), 'Transaction due to approval of inventory adjustment', inv.transdate, 'invadj-' || in_id, true, 'ia', 'inventory_report') RETURNING id INTO t_trans_id;
UPDATE inventory_report set trans_id = t_trans_id WHERE id = in_id;
-- When the count is lower than expected, we need to write-off -- some of our inventory. To do so, we post COGS to the expense -- account and reduce the inventory by the amount. There is no -- income associated with the transaction, so 'discount' == 100% -- -- When the count is higher than expected, we need to increase -- some of our inventory. To do so, we stock at reverse COGS, taking -- the cost of the inventory increase out of COGS (to re-add on sale) INSERT INTO invoice (trans_id, parts_id, description, qty, allocated, sellprice, precision, discount) SELECT t_trans_id, p.id, p.description, l.variance * -1, 0, 0, 3, 1 FROM parts p JOIN inventory_report_line l ON p.id = l.parts_id WHERE l.adjust_id = in_id;
-- cogs for AR manipulates the tip of the FIFO buffer -- record shortage as a regular FIFO allocation, without income aspects -- record overage as a regular FIFO reversal, similarly without income aspects PERFORM cogs__add_for_ar_line(id) FROM invoice WHERE trans_id = t_trans_id;
UPDATE parts p SET onhand = onhand + (select variance from inventory_report_line l where p.id = l.parts_id and l.adjust_id = in_id) WHERE id IN (select parts_id from inventory_report_line where adjust_id = in_id);
SELECT * INTO inv FROM inventory_report where id = in_id;
RETURN inv;
END;

