  EXECUTE 'GRANT USAGE ON SCHEMA ' || quote_ident(in_schema) || ' TO ' || quote_ident(lsmb__role('base_user'));
  RETURN true;
END;

section.para:
Function Properties
Language Return Type
PLPGSQL boolean
This file restores the roles from lsmb__backup_roles() and then cleares the role backup. If the role backup/restore did not work properly one can always restore the backup tables only from the backup again but this reduces security disclosure.
DECLARE temp_rec RECORD;
BEGIN
FOR temp_rec IN select u.username, l.* FROM users u JOIN lsmb_password_backups l ON u.id = l.id LOOP PERFORM 1 FROM pg_authid WHERE rolname = temp_rec.username;
IF FOUND THEN EXECUTE $e$ ALTER USER $e$ || quote_ident(temp_rec.username) || $e$ WITH ENCRYPTED PASSWORD $e$ || quote_literal(temp_rec.rolpassword) || $e$ VALID UNTIL $e$ || coalesce(quote_literal(temp_rec.rolvaliduntil), 'NULL'); ELSE EXECUTE $e$ CREATE USER $e$ || quote_ident(temp_rec.username) || $e$ WITH ENCRYPTED PASSWORD $e$ || quote_literal(temp_rec.rolpassword) || $e$ VALID UNTIL $e$ || coalesce(quote_literal(temp_rec.rolvaliduntil), 'NULL'); END IF; END LOOP;
PERFORM admin__add_user_to_role(u.username, r.rolname) FROM users u JOIN lsmb_role_grants r ON u.id = r.id JOIN pg_authid a ON r.rolname = a.rolname;
RETURN lsmb__clear_role_backup();
END;

para.programlisting:
DECLARE t_mfg_lot mfg_lot;
BEGIN
    SELECT * INTO t_mfg_lot FROM mfg_lot WHERE id = $1;
    IF NOT FOUND THEN
        RAISE EXCEPTION 'Lot not found';
    END IF;

    UPDATE parts SET onhand = onhand
                              - (select qty from mfg_lot_item
                                  WHERE parts_id = parts.id AND
                                        mfg_lot_id = $1)
     WHERE id in (select parts_id from mfg_lot_item
                   WHERE mfg_lot_id = $1);

    UPDATE parts SET onhand = onhand + t_mfg_lot.qty
     where id = t_mfg_lot.parts_id;

    INSERT INTO transactions (id, reference, description, transdate, approved,
                   trans_type_code, table_name)
    values (nextval('id'), 'mfg-' || $1::TEXT, 'Manufacturing lot',
            now(), true, 'as', 'mfg_lot');

    INSERT INTO invoice (trans_id, parts_id, qty, allocated)
    SELECT currval('id')::int, parts_id, qty, 0
      FROM mfg_lot_item WHERE mfg_lot_id = $1;

    PERFORM cogs__add_for_ar_line(id) FROM invoice
      WHERE trans_id = currval('id')::int;


    PERFORM * FROM invoice
      WHERE qty + allocated <> 0 AND trans_id = currval('id')::int;

    IF FOUND THEN
       RAISE EXCEPTION 'Not enough parts in stock';
    END IF;

    INSERT INTO invoice (trans_id, parts_id, qty, allocated, sellprice)
    SELECT currval('id')::int, t_mfg_lot.parts_id, t_mfg_lot.qty * -1, 0,
           -1*sum(amount_bc) / t_mfg_lot.qty
      FROM acc_trans
     WHERE amount_bc < 0 and trans_id = currval('id')::int;

    PERFORM cogs__add_for_ap_line(currval('invoice_id_seq')::int);

    -- move from COGS back into inventory
    INSERT INTO acc_trans(trans_id, chart_id, transdate,
                          amount_bc, curr, amount_tc,
                          invoice_id, memo)
    SELECT trans_id, chart_id, transdate,
           amount_bc * -1, curr, amount_tc * -1,
           currval('invoice_id_seq')::int, 'Collect assembly parts costs from COGS'
      FROM acc_trans
     WHERE amount_bc < 0 and trans_id = currval('id')::int;

    -- difference goes into inventory
    INSERT INTO acc_trans(trans_id, transdate, chart_id,
                          amount_bc, curr, amount_tc,
                          invoice_id, memo)
    SELECT trans_id, now(), (select inventory_accno_id
                               from parts
                              where id = t_mfg_lot.parts_id),
           sum(amount_bc) * -1, curr, sum(amount_tc) * -1,
           currval('invoice_id_seq')::int, 'Post assembly parts cost in inventory'
      FROM acc_trans
     WHERE trans_id = currval('id')::int
  GROUP BY trans_id, curr;


    RETURN t_mfg_lot.qty;
END;

DECLARE retval oe; ordercount int; ids int[]; loop_info record; settings text[]; my_person_id int; BEGIN
SELECT id INTO my_person_id FROM person WHERE entity_id = person__get_my_entity_id();
settings := ARRAY['sonumber', 'ponumber', 'sqnumber', 'rfqnumber']; ids := array[]::int[];
-- This approach of looping through insert/select operations will break down -- if overly complex order consolidation jobs are run (think, hundreds of -- combined orders in the *output* -- -- The tradeoff is that if we address the huge complex runs here, then we have -- the possibility of having to lock the whole table which poses other issues. -- For that reason, I am going with this approach for now. --CT
FOR loop_info IN SELECT max(id) as id, taxincluded, entity_credit_account, oe_class_id, curr FROM oe WHERE id = any(in_ids) GROUP BY taxincluded, entity_credit_account, oe_class_id, curr LOOP
INSERT INTO oe (ordnumber, transdate, amount_tc, netamount_tc, reqdate, taxincluded, shippingpoint, notes, curr, person_id, closed, quotation, quonumber, intnotes, shipvia, language_code, ponumber, terms, oe_class_id, entity_credit_account) SELECT CASE WHEN oe_class_id IN (1, 2) THEN setting_increment(settings[oe_class_id]) ELSE NULL END, now()::date, sum(amount_tc), sum(netamount_tc), min(reqdate), taxincluded, min(shippingpoint), '', curr, my_person_id, false, false, CASE WHEN oe_class_id IN (3, 4) THEN setting_increment(settings[oe_class_id]) ELSE NULL END, NULL, NULL, NULL, null, min(terms), oe_class_id, entity_credit_account FROM oe WHERE id = any (in_ids) AND taxincluded = loop_info.taxincluded AND entity_credit_account = loop_info.entity_credit_account AND oe_class_id = loop_info.oe_class_id GROUP BY curr, taxincluded, oe_class_id, entity_credit_account;
INSERT INTO orderitems (trans_id, parts_id, description, qty, sellprice, precision, discount, unit, reqdate, ship, serialnumber, notes) SELECT currval('oe_id_seq'), oi.parts_id, oi.description, oi.qty, oi.sellprice, oi.precision, oi.discount, oi.unit, oi.reqdate, oi.ship, oi.serialnumber, oi.notes FROM orderitems oi JOIN oe ON oi.trans_id = oe.id WHERE oe.id = any (in_ids) AND taxincluded = loop_info.taxincluded AND entity_credit_account = loop_info.entity_credit_account AND oe_class_id = loop_info.oe_class_id;
ids := ids || currval('oe_id_seq')::int;
END LOOP;
UPDATE oe SET closed = true WHERE id = any(in_ids);
FOR retval IN select * from oe WHERE id =any(ids) LOOP RETURN NEXT retval; END LOOP;
END;

