Function Properties
Language Return Type
PLPGSQL integer
Reverses the payment identified by `in_payment_id`, adding the resulting transactions into `in_batch_id` if that''s not null.
Returns the `id` of the reversal payment generated.
DECLARE t_payment_id int; BEGIN -- check against being an overpayment?? INSERT INTO payment (reference, trans_id, payment_class, payment_date, closed, entity_credit_id, employee_id, currency, reversing, notes) SELECT reference, trans_id, payment_class, in_payment_date, closed, entity_credit_id, person__get_my_id(), currency, in_payment_id, 'This payment reverses ' || in_payment_id FROM payment WHERE id = in_payment_id RETURNING id INTO t_payment_id;
IF in_batch_id IS NOT NULL THEN -- Note that we're using the original payment to derive the -- value for 'trans_id', because the reversal inserts into the -- same trans_id (see 'new_entries' query below for determination -- of the value of 'trans_id' in the new acc_trans lines) INSERT INTO voucher (trans_id, batch_id, batch_class) select trans_id, in_batch_id, (select case when payment_class = 1 then 4 else 7 end from payment c where c.id = t_payment_id) from acc_trans a join payment_links pl on a.entry_id = pl.entry_id where pl.payment_id = in_payment_id group by trans_id; END IF;
-- Using a CTE because we can use the returned result to fill -- the payment_links table without further temporary tables WITH new_entries AS ( INSERT INTO acc_trans (trans_id, chart_id, transdate, source, cleared, memo, invoice_id, approved, amount_bc, amount_tc, curr, voucher_id) SELECT trans_id, chart_id, in_payment_date, source, false, memo, null, coalesce(in_approved, true), -1 * amount_bc, -1 * amount_tc, curr, (select id from voucher v where a.trans_id = v.trans_id and v.batch_id = in_batch_id) as voucher_id FROM acc_trans a WHERE exists (select 1 from payment_links pl where pl.payment_id = in_payment_id and a.entry_id = pl.entry_id) RETURNING entry_id ) INSERT INTO payment_links (payment_id, entry_id) SELECT t_payment_id, entry_id FROM new_entries;
RETURN t_payment_id; END;

Function Properties
Language Return Type
PLPGSQL integer
This posts the payments for large batch workflows.
Note that in_transactions is a two-dimensional numeric array. Of each sub-array, the first element is the (integer) transaction id, and the second is the amount for that transaction.
DECLARE out_count int; t_voucher_id int; t_trans_id int; t_amount numeric; t_ar_ap_id int; t_cash_id int; t_defaultcurr text; t_exchangerate numeric; t_cash_sign int; t_batch batch; BEGIN t_exchangerate := in_exchangerate;
IF in_batch_id IS NULL THEN -- t_voucher_id := NULL; RAISE EXCEPTION 'Bulk Post Must be from Batch!'; ELSE SELECT * INTO t_batch FROM batch WHERE in_batch_id = id; IF t_batch.approved_by IS NOT NULL THEN RAISE EXCEPTION 'Approved Batch'; ELSIF t_batch.locked_by IS NOT NULL THEN PERFORM * FROM session JOIN users ON (session.users_id = users.id) WHERE session_id = t_batch.locked_by AND users.username = SESSION_USER;
IF NOT FOUND THEN -- locked by someone else RAISE EXCEPTION 'batch locked by %, I am %', t_batch.locked_by, session_user; END IF; END IF; INSERT INTO voucher (batch_id, batch_class, trans_id) values (in_batch_id, (SELECT batch_class_id FROM batch WHERE id = in_batch_id), in_transactions[1][1]);
t_voucher_id := currval('voucher_id_seq'); END IF;
SELECT * INTO t_defaultcurr FROM defaults_get_defaultcurrency();
IF in_account_class = 1 THEN t_cash_sign := 1; ELSE t_cash_sign := -1; END IF;
IF (in_currency IS NULL OR in_currency = t_defaultcurr) THEN t_exchangerate := 1; END IF; IF t_exchangerate IS NULL THEN RAISE EXCEPTION 'No exchangerate provided and not default currency'; END IF;
CREATE TEMPORARY TABLE bulk_payments_in ( id int, -- AR/AP id payment_id int, -- payment.id eca_id int, -- entity_credit_account.id entry_id int, -- acc_trans.entry_id amount_bc numeric, -- amount in local currency (current rate) amount_tc numeric, -- amount in foreign currency disc_amount_bc numeric, -- discount amount in disc_amount_tc numeric, fxrate numeric, gain_loss_accno int, want_gain_loss_accno boolean, invoice_date date);
FOR out_count IN array_lower(in_transactions, 1) .. array_upper(in_transactions, 1) LOOP -- Fill the bulk payments table IF in_transactions[out_count][2] <> 0 THEN INSERT INTO bulk_payments_in(id, amount_tc) VALUES (in_transactions[out_count][1], in_transactions[out_count][2]); END IF; END LOOP;
UPDATE bulk_payments_in bpi SET eca_id = (SELECT entity_credit_account FROM ar WHERE in_account_class = 2 AND bpi.id = ar.id UNION SELECT entity_credit_account FROM ap WHERE in_account_class = 1 AND bpi.id = ap.id);
CREATE TEMPORARY TABLE eca_payments_in AS SELECT eca_id, nextval('payment_id_seq') as payment_id, -- this logic is reversed, but mirrors what's been -- incorrect in payment post since 12 years... case when in_account_class = 1 then setting_increment('rcptnumber') else setting_increment('paynumber') end as reference FROM bulk_payments_in GROUP BY eca_id;
UPDATE bulk_payments_in bpi SET payment_id = (select payment_id from eca_payments_in ep where bpi.eca_id = ep.eca_id);
UPDATE bulk_payments_in bpi SET invoice_date = (select transdate from transactions trn where trn.id = bpi.id);
IF (in_currency IS NULL OR in_currency = t_defaultcurr) THEN UPDATE bulk_payments_in SET fxrate = 1; ELSE UPDATE bulk_payments_in SET fxrate = (SELECT fxrate
FROM (SELECT id, CASE WHEN amount_tc<>0 THEN amount_bc/amount_tc ELSE NULL END as fxrate FROM ar UNION SELECT id, CASE WHEN amount_tc<>0 THEN amount_bc/amount_tc ELSE NULL END as fxrate FROM ap) a WHERE a.id = bulk_payments_in.id);
UPDATE bulk_payments_in SET want_gain_loss_accno = true, gain_loss_accno = (SELECT value::int FROM defaults WHERE setting_key = 'fxgain_accno_id') WHERE ((t_exchangerate - bulk_payments_in.fxrate) * t_cash_sign) < 0;
UPDATE bulk_payments_in SET want_gain_loss_accno = true, gain_loss_accno = (SELECT value::int FROM defaults WHERE setting_key = 'fxloss_accno_id') WHERE ((t_exchangerate - bulk_payments_in.fxrate) * t_cash_sign) > 0; -- explicitly leave zero gain/loss accno_id entries at NULL -- so we have an easy check later END IF;
PERFORM * FROM bulk_payments_in WHERE want_gain_loss_accno AND gain_loss_accno IS NULL; IF FOUND THEN RAISE 'Missing gain/loss account while posting FX difference'; END IF;
UPDATE bulk_payments_in bpi SET disc_amount_tc = coalesce( (SELECT bpi.amount_tc / (100 - eca.discount::numeric) * eca.discount::numeric FROM entity_credit_account eca WHERE age(in_payment_date, bpi.invoice_date) < (eca.discount_terms||' days')::interval AND eca.discount_terms IS NOT NULL AND eca.discount IS NOT NULL AND eca.discount_account_id IS NOT NULL AND eca.id = bpi.eca_id), 0);
UPDATE bulk_payments_in SET amount_bc = amount_tc * t_exchangerate, disc_amount_bc = disc_amount_tc * t_exchangerate;
select id into t_ar_ap_id from account where accno = in_ar_ap_accno; select id into t_cash_id from account where accno = in_cash_accno;
-- Given an open item, created on an earlier date, at an FX rate of '2', -- the code below inserts this transaction (for each line in bulk_payments_in), -- with the FX rate of the current transaction being '3' and the terms of the -- transaction being 10/30 and the transaction being paid within the term.
-- | Credits | Debits | FX | -- | BC | TC | BC | TC | rate | -- ----------------+--------+--------+--------+--------+-------| -- Current account | 81 | 27 | | | curr | -- Discounts | 9 | 3 | | | curr | -- ----------------+--------+--------+--------+--------+-------| -- Accounts rec | | | 60 | 30 | orig | -- FX effects | | | 30 | 0 | na | -- ----------------+--------+--------+--------+--------+-------| -- Total | 90 | 30 | 90 | 30 | | -- ----------------+--------+--------+--------+--------|-------|
-- Note: due to the fact that the discount is valued at the current rate, -- the revaluation is based on the accounts receivable amount. -- If the discount amount were to be valued at the original rate, -- the FX effect should be calculated based on the current payment amount
-- The 'id' values were allocated above INSERT INTO payment (id, reference, payment_class, payment_date, entity_credit_id, employee_id, currency, notes) SELECT epi.payment_id, epi.reference, in_account_class, in_payment_date, epi.eca_id, person__get_my_id(), in_currency, 'generated from bulk payment' FROM eca_payments_in epi;
-- Insert cash side @ current fx rate UPDATE bulk_payments_in SET entry_id = nextval('acc_trans_entry_id_seq') WHERE amount_tc <> 0; INSERT INTO acc_trans (trans_id, chart_id, amount_bc, curr, amount_tc, approved, voucher_id, transdate, source, entry_id) SELECT id, t_cash_id, amount_bc * t_cash_sign, in_currency, amount_tc * t_cash_sign, CASE WHEN t_voucher_id IS NULL THEN true ELSE false END, t_voucher_id, in_payment_date, in_source, entry_id FROM bulk_payments_in where amount_tc <> 0; INSERT INTO payment_links (payment_id, entry_id, type) SELECT payment_id, entry_id, 1 FROM bulk_payments_in WHERE amount_tc <> 0;
-- Insert discount @ current fx rate UPDATE bulk_payments_in SET entry_id = nextval('acc_trans_entry_id_seq') WHERE disc_amount_bc <> 0; INSERT INTO acc_trans (trans_id, chart_id, amount_bc, curr, amount_tc, approved, voucher_id, transdate, source, entry_id) SELECT bpi.id, eca.discount_account_id, disc_amount_bc * t_cash_sign, in_currency, disc_amount_tc * t_cash_sign, CASE WHEN t_voucher_id IS NULL THEN true ELSE false END, t_voucher_id, in_payment_date, in_source, entry_id FROM bulk_payments_in bpi JOIN entity_credit_account eca ON bpi.eca_id = eca.id WHERE bpi.disc_amount_bc <> 0; INSERT INTO payment_links (payment_id, entry_id, type) SELECT payment_id, entry_id, 1 FROM bulk_payments_in WHERE disc_amount_bc <> 0;
-- Insert AR/AP amount @ orginal rate UPDATE bulk_payments_in SET entry_id = nextval('acc_trans_entry_id_seq'); INSERT INTO acc_trans (trans_id, chart_id, amount_bc, curr, amount_tc, approved, voucher_id, transdate, source, entry_id) SELECT bpi.id, t_ar_ap_id, (bpi.amount_tc + bpi.disc_amount_tc) * t_cash_sign * -1 * bpi.fxrate, in_currency, (bpi.amount_tc + bpi.disc_amount_tc) * t_cash_sign * -1, CASE WHEN t_voucher_id IS NULL THEN true ELSE false END, t_voucher_id, in_payment_date, in_source, entry_id FROM bulk_payments_in bpi JOIN entity_credit_account eca ON bpi.eca_id = eca.id; INSERT INTO payment_links (payment_id, entry_id, type) SELECT payment_id, entry_id, 1 FROM bulk_payments_in;
-- Insert fx gain/loss effects, if applicable UPDATE bulk_payments_in SET entry_id = nextval('acc_trans_entry_id_seq') WHERE gain_loss_accno IS NOT NULL; INSERT INTO acc_trans (trans_id, chart_id, amount_bc, curr, amount_tc, approved, voucher_id, transdate, source, entry_id) SELECT id, gain_loss_accno, amount_tc * t_cash_sign * (t_exchangerate - fxrate), in_currency, 0, CASE WHEN t_voucher_id IS NULL THEN true ELSE false END, t_voucher_id, in_payment_date, in_source, entry_id FROM bulk_payments_in WHERE gain_loss_accno IS NOT NULL; INSERT INTO payment_links (payment_id, entry_id, type) SELECT payment_id, entry_id, 1 FROM bulk_payments_in WHERE gain_loss_accno IS NOT NULL;
DROP TABLE bulk_payments_in; DROP TABLE eca_payments_in; perform unlock_all(); return out_count; END;

Function Properties
Language Return Type
PLPGSQL integer
Posts a payment. in_op_* arrays are cross-indexed with eachother. Other arrays are cross-indexed with eachother.
The 'in_cash_account_id's are the "cash side" of the payment; i.e. this can be a bank current account, overpayment account or a suspense account associated with a bank current account.
DECLARE var_payment_id int; DECLARE var_gl_id int; DECLARE var_entry record; DECLARE var_entry_id int[]; DECLARE out_count int; DECLARE coa_id record; DECLARE var_employee int; DECLARE var_account_id int; DECLARE default_currency char(3); DECLARE current_exchangerate numeric; DECLARE old_exchangerate numeric; DECLARE fx_gain_loss_amount numeric; DECLARE gain_loss_accno_id int; DECLARE sign int; BEGIN IF array_upper(in_amount, 1) <> array_upper(in_cash_account_id, 1) THEN RAISE EXCEPTION 'Wrong number of accounts'; END IF;
current_exchangerate := in_exchangerate; IF in_account_class = 1 THEN sign := 1; ELSE sign := -1; END IF; SELECT * INTO default_currency FROM defaults_get_defaultcurrency();
SELECT INTO var_employee p.id FROM users u JOIN person p ON (u.entity_id=p.entity_id) WHERE username = SESSION_USER LIMIT 1;
-- WE HAVE TO INSERT THE PAYMENT, USING THE GL INFORMATION -- THE ID IS GENERATED BY payment_id_seq INSERT INTO payment (reference, payment_class, payment_date, employee_id, currency, notes, entity_credit_id) VALUES (-- the rcptnumber and paynumber are reversed; have been for 12 years (CASE WHEN in_account_class = 1 THEN setting_increment('rcptnumber') ELSE setting_increment('paynumber') END), in_account_class, in_datepaid, var_employee, in_curr, in_notes, in_entity_credit_id);
-- Assuming a transaction with foreign currency being recorded, -- at an exchangerate of 3 upon AR creation and an exchangerate of 2 -- upon payment. The owed (and paid) amount is 20 in the foreign currency.
-- 5000 = 'AR' account -- 5100 = 'Cash' account -- 9999 = fx gain/loss account
-- +-------+----------+----------+----------+----------+ -- | accno | Deb (bc) | Deb (tc) | Cre (bc) | Cre (tc) | -- +-------+----------+----------+----------+----------+ -- | 5000 | | | 60.00 | 20.00 | -- +-------+----------+----------+----------+----------+ -- | 5100 | 40.00 | 20.00 | | | -- +-------+----------+----------+----------+----------+ -- | 9999 | 20.00 | 00.00 | | | -- +-------+----------+----------+----------+----------+
-- +-------+----------+----------+----------+----------+ -- | Total | 60.00 | 20.00 | 60.00 | 20.00 | -- +-------+----------+----------+----------+----------+
SELECT currval('payment_id_seq') INTO var_payment_id; IF (array_upper(in_cash_account_id, 1) > 0) THEN FOR out_count IN array_lower(in_cash_account_id, 1) .. array_upper(in_cash_account_id, 1) LOOP -- Insert cash account side of the payment -- Each payment can have its own cash account set through the UI INSERT INTO acc_trans (chart_id, amount_bc, curr, amount_tc, trans_id, transdate, approved, source, memo) VALUES (in_cash_account_id[out_count], in_amount[out_count]*current_exchangerate*sign, in_curr, in_amount[out_count]*sign, in_transaction_id[out_count], in_datepaid, coalesce(in_approved, true), in_source[out_count], in_memo[out_count]); -- Link the ledger line to the payment record INSERT INTO payment_links VALUES (var_payment_id, currval('acc_trans_entry_id_seq'), 1); IF (in_ovp_payment_id IS NOT NULL AND in_ovp_payment_id[out_count] IS NOT NULL) THEN -- mark the current transaction as being the consequence of an overpayment -- (lowering the customer account balance) INSERT INTO payment_links VALUES (in_ovp_payment_id[out_count], currval('acc_trans_entry_id_seq'), 0); END IF; END LOOP;
-- HANDLE THE AR/AP ACCOUNTS -- OBTAIN THE ACCOUNT AND EXCHANGERATE FROM THERE FOR out_count IN array_lower(in_transaction_id, 1) .. array_upper(in_transaction_id, 1) LOOP SELECT chart_id, amount_bc/amount_tc INTO var_account_id, old_exchangerate FROM acc_trans as ac JOIN account_link as l ON (l.account_id = ac.chart_id) WHERE trans_id = in_transaction_id[out_count] AND ( l.description in ('AR', 'AP'));
-- Now we post the AP/AR transaction INSERT INTO acc_trans (chart_id, amount_bc, curr, amount_tc, trans_id, transdate, approved, source, memo) VALUES (var_account_id, in_amount[out_count]*old_exchangerate*sign*-1, in_curr, in_amount[out_count]*sign*-1, in_transaction_id[out_count], in_datepaid, coalesce(in_approved, true), in_source[out_count], in_memo[out_count]); -- Link the ledger line to the payment record INSERT INTO payment_links VALUES (var_payment_id, currval('acc_trans_entry_id_seq'), 1);
-- Calculate the gain/loss on the transaction -- everything above depends on this being an AR/AP posting -- the PNL posting and decision to post a gain or loss does not -- --> incorporate sign here instead of when posting. fx_gain_loss_amount := in_amount[out_count]*sign*(old_exchangerate-current_exchangerate);
IF (fx_gain_loss_amount > 0) THEN SELECT value::int INTO gain_loss_accno_id FROM defaults WHERE setting_key = 'fxgain_accno_id'; ELSIF (fx_gain_loss_amount < 0) THEN SELECT value::int INTO gain_loss_accno_id FROM defaults WHERE setting_key = 'fxloss_accno_id'; END IF; IF fx_gain_loss_amount <> 0.00 THEN INSERT INTO acc_trans (chart_id, amount_bc, curr, amount_tc, trans_id, transdate, approved, source) -- In this transaction we can't use the default currency, -- because by definition the tc and bc amounts are the same. VALUES (gain_loss_accno_id, fx_gain_loss_amount, in_curr, 0, -- the transaction currency side is zero by definition in_transaction_id[out_count], in_datepaid, coalesce(in_approved, true), in_source[out_count]);
INSERT INTO payment_links VALUES (var_payment_id, currval('acc_trans_entry_id_seq'), 1); END IF; END LOOP; END IF;
-- -- HANDLE THE OVERPAYMENTS NOW IF (array_upper(in_op_cash_account_id, 1) > 0) THEN INSERT INTO transactions (reference, description, transdate, entered_by, approved, trans_type_code, table_name) VALUES (setting_increment('glnumber'), in_gl_description, in_date_paid, var_employee, in_approved, 'op', 'payment'); SELECT currval('id') INTO var_gl_id;
UPDATE payment SET trans_id = var_gl_id WHERE id = var_payment_id;
FOR out_count IN array_lower(in_op_cash_account_id, 1) .. array_upper(in_op_cash_account_id, 1) LOOP -- Cash account side of the transaction INSERT INTO acc_trans (chart_id, amount_bc, curr, amount_tc, trans_id, transdate, approved, source, memo) VALUES (in_op_cash_account_id[out_count], in_op_amount[out_count]*current_exchangerate*sign, in_curr, in_op_amount[out_count]*sign, var_gl_id, in_datepaid, coalesce(in_approved, true), in_op_source[out_count], in_op_memo[out_count]); INSERT INTO payment_links VALUES (var_payment_id, currval('acc_trans_entry_id_seq'), 2);
END LOOP;
-- NOW LETS HANDLE THE OVERPAYMENT ACCOUNTS FOR out_count IN array_lower(in_op_account_id, 1) .. array_upper(in_op_account_id, 1) LOOP INSERT INTO acc_trans (chart_id, amount_bc, curr, amount_tc, trans_id, transdate, approved, source, memo) VALUES (in_op_account_id[out_count], in_op_amount[out_count]*current_exchangerate*sign*-1, in_curr, in_op_amount[out_count]*sign*-1, var_gl_id, in_datepaid, coalesce(in_approved, true), in_op_source[out_count], in_op_memo[out_count]); INSERT INTO payment_links VALUES (var_payment_id, currval('acc_trans_entry_id_seq'), 2); END LOOP; END IF; return var_payment_id; END;

