DECLARE retval gl_report_item; t_balance numeric; t_chart_id int; BEGIN
IF in_from_date IS NULL THEN t_balance := 0; ELSIF in_accno IS NOT NULL THEN SELECT id INTO t_chart_id FROM account WHERE accno = in_accno; t_balance := account__obtain_balance((in_from_date - '1 day'::interval)::date, (select id from account where accno = in_accno)); ELSE t_balance := null; END IF;
FOR retval IN WITH RECURSIVE bu_tree (id, path) AS ( SELECT id, ARRAY[id]::int[] AS path FROM business_unit WHERE parent_id is null UNION SELECT bu.id, array_append(bu_tree.path, bu.id) FROM business_unit bu JOIN bu_tree ON bu_tree.id = bu.parent_id ) SELECT g.id, g.type, g.invoice, g.reference, g.eca_name, g.description, ac.transdate, ac.source, ac.amount_bc, ac.curr, ac.amount_tc, c.accno, c.gifi_accno, ac.cleared, ac.memo, c.description AS accname, ac.chart_id, ac.entry_id, sum(ac.amount_bc) over (order by ac.transdate, ac.trans_id, c.accno, ac.entry_id) + t_balance as running_balance, array_agg(ARRAY[bac.class_id, bac.bu_id]) FROM (select txn.id, trans_type_code as type, coalesce(ar.invoice, ap.invoice, false) as invoice, coalesce(ar.invnumber, ap.invnumber, reference) as reference, coalesce(ar.entity_name, ap.entity_name, null::text) as eca_name, description, txn.approved FROM transactions txn LEFT JOIN (select ar.*, entity.name as entity_name from ar join entity_credit_account eca on ar.entity_credit_account = eca.id join entity on eca.entity_id = entity.id) ar ON txn.id = ar.id LEFT JOIN (select ap.*, entity.name as entity_name from ap join entity_credit_account eca on ap.entity_credit_account = eca.id join entity on eca.entity_id = entity.id) ap ON txn.id = ap.id) g JOIN acc_trans ac ON ac.trans_id = g.id JOIN account c ON ac.chart_id = c.id LEFT JOIN business_unit_ac bac ON ac.entry_id = bac.entry_id LEFT JOIN bu_tree ON bac.bu_id = bu_tree.id WHERE (g.reference ilike in_reference || '%' or in_reference is null) AND (c.accno = in_accno OR in_accno IS NULL) AND (ac.source ilike '%' || in_source || '%' OR in_source is null) AND (ac.memo ilike '%' || in_memo || '%' OR in_memo is null) AND (in_description IS NULL OR g.description @@ plainto_tsquery(get_default_lang()::regconfig, in_description)) AND (transdate BETWEEN in_from_date AND in_to_date OR (transdate >= in_from_date AND in_to_date IS NULL) OR (transdate <= in_to_date AND in_from_date IS NULL) OR (in_to_date IS NULL AND in_from_date IS NULL)) AND (in_approved is null or (in_approved is true and g.approved is true AND ac.approved is true) or (in_approved is false and (g.approved is false or ac.approved is false))) AND (in_voided is null or in_voided is not distinct from (exists (select 1 from transactions t where t.approved and t.reversing = g.id))) AND (in_from_amount IS NULL OR abs(ac.amount_bc) >= in_from_amount) AND (in_to_amount IS NULL OR abs(ac.amount_bc) <= in_to_amount) AND (in_category = c.category OR in_category IS NULL) GROUP BY g.id, g.type, g.invoice, g.reference, g.eca_name, g.description, ac.transdate, ac.source, ac.amount_bc, c.accno, c.gifi_accno, ac.cleared, ac.memo, c.description, ac.chart_id, ac.entry_id, ac.trans_id HAVING in_business_units is null or in_business_units <@ compound_array(bu_tree.path) ORDER BY ac.transdate, ac.trans_id, c.accno, ac.entry_id LOOP RETURN NEXT retval; END LOOP; END;

